import React from 'react'
import {FaTimes} from 'react-icons/fa'
const EachTask = ({task, onDelete}) => {
    return (
        <div className ="task">
            <h3>{task.text} <FaTimes onClick = {onDelete}/></h3>
            <p>{task.day}</p>
        </div>
    )
}

export default EachTask
